mapper.select:
SELECT uam.user_award_id, uam.create_time, uam.used_status, uam.point, buyer.user_id, buyer.name, s.shop_id, s.shop_name, a.award_id, a.award_name, a.award_img, a.award_desc, operator.user_id AS operator_id, operator.name AS operator_name FROM tb_user_award_map uam, tb_person_inf buyer, tb_shop s, tb_award a, tb_person_inf operator
uam.user_id = buyer.user_id and uam.shop_id = s.shop_id and uam.award_id = a.award_id and uam.operator_id = operator.user_id
and uam.user_id = #{userAwardCondition.user.userId}
and uam.shop_id = #{userAwardCondition.shop.shopId}
and buyer.name like '%${userAwardCondition.user.name}%'
and a.award_name like '%${userAwardCondition.award.awardName}%'
and a.enableStatus = #{userAwardCondition.award.enableStatus}
ORDER BY uam.create_time DESC LIMIT #{rowIndex},#{pageSize};

SELECT count(1) FROM tb_user_award_map uam, tb_person_inf buyer, tb_shop s, tb_award a, tb_person_inf operator
uam.user_id = buyer.user_id and uam.shop_id = s.shop_id and uam.award_id = a.award_id and uam.operator_id = operator.user_id
and uam.user_id = #{userAwardCondition.user.userId}
and uam.shop_id = #{userAwardCondition.shop.shopId}
and buyer.name like '%${userAwardCondition.user.name}%'
and a.award_name like '%${userAwardCondition.award.awardName}%'
and a.enableStatus = #{userAwardCondition.award.enableStatus}

mapper.update:

UPDATE
tb_user_award_map
SET
used_status = #{usedStatus}
WHERE
user_award_id=#{userAwardId}
AND user_id = #{user.userId}
